var Errors = {};
if( module ) module.exports = Errors;
function Error(code, message) {
    this.code = code || 0;
    this.message = message || 'N/A';
}
Error.prototype.toString = function() {
    return 'Error ' + this.code + ': '+ this.message;
}

// 0xx - WTF
Errors.UNKNOWN_ERROR        = new Error(001, 'An unknown error occurred');

// 1xx - Authentication
Errors.INVALID_CREDENTIALS  = new Error(100, 'Invalid credentials');
Errors.EMPTY_TOKEN          = new Error(101, 'Provided token is empty');

// 2xx - Data
Errors.CANT_MAKE_TOKEN      = new Error(200, 'Unable to generate an authentication token');
Errors.DATA_NOT_FOUND       = new Error(202, 'Data not found');
